    <script>
        $(document).ready(function() {
    // AJAX search request
    $("#search-btn").click(function() {
        var searchTerm = $("#search-input").val();
        if (searchTerm.trim() !== "") {
            $.ajax({
                type: "POST",
                url: "search.php",
                data: { search: searchTerm },
                success: function(response) {
                    $("#search-results").html(response);
                }
            });
        }
    });

});
</script>



